import styles from './Search.module.scss'
import { faMagnifyingGlass } from '@fortawesome/free-solid-svg-icons';
import Proper from '../../Proper';

function SearchRender(prop) {


  let listResultRender = (value) => {
    if (value) {
      return (
        <>
          {prop.list.map((result, index) => {
            if (prop.searchValue) {
              if (result.name.toLowerCase().includes(value.toLowerCase()) || result.name.toLowerCase() === value.toLowerCase()) {
                return (
                  <Proper key={index} searchValue={result.name} icon={faMagnifyingGlass} avatar={result.avatar} />
                );
              }
            }
          })}
          <p key={value} className={styles['show-result']}>View all results for "{value}"</p>
        </>
      );
    }
  }

  return (
    <div className={styles['search-list']}>
      {listResultRender(prop.searchValue)}
    </div>
    
  );
}

export default SearchRender;
