import axios from "axios";

const users = [
    { name: "Tung", avatar: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQnuXB-rgZwsrCtrXW-DwV4b6mjNZqTssy5aQ&usqp=CAU' },
    { name: "kieu", avatar: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQnuXB-rgZwsrCtrXW-DwV4b6mjNZqTssy5aQ&usqp=CAU' },
    { name: "tu", avatar: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQnuXB-rgZwsrCtrXW-DwV4b6mjNZqTssy5aQ&usqp=CAU' },
    { name: "vy", avatar: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQnuXB-rgZwsrCtrXW-DwV4b6mjNZqTssy5aQ&usqp=CAU' },
    { name: 'iuuu', avatar: 'https://cdn.24h.com.vn/upload/2-2021/images/2021-05-22/anh-8-1621645023-458-width650height813.jpg' },
    { name: 'iuuu', avatar: 'https://cdn.24h.com.vn/upload/2-2021/images/2021-05-22/anh-8-1621645023-458-width650height813.jpg' },
];

const loginUsers = [
    {username: "TungPT", password: "TungPT12345", avatar: "https://scontent.fsgn2-2.fna.fbcdn.net/v/t1.6435-9/71101379_944636199227991_5193408517985271808_n.jpg?_nc_cat=103&ccb=1-7&_nc_sid=09cbfe&_nc_ohc=2Z5nTd0Vd5IAX9itS0K&tn=EzqlHbKIHtNvnywS&_nc_ht=scontent.fsgn2-2.fna&oh=00_AfAGPYp_EJFOaWy0lwdVwaLlot8OUP9NWGSZZxlioaW_kQ&oe=63E0F29F"},
    {username: "Tung", password: "123456", avatar: "https://scontent.fsgn2-2.fna.fbcdn.net/v/t1.6435-9/71101379_944636199227991_5193408517985271808_n.jpg?_nc_cat=103&ccb=1-7&_nc_sid=09cbfe&_nc_ohc=2Z5nTd0Vd5IAX9itS0K&tn=EzqlHbKIHtNvnywS&_nc_ht=scontent.fsgn2-2.fna&oh=00_AfAGPYp_EJFOaWy0lwdVwaLlot8OUP9NWGSZZxlioaW_kQ&oe=63E0F29F"}
];

const searchUser = (value) => {
    let listResult = users.filter( user => {
        return user.name.toLowerCase().includes(value.toLowerCase()) ? true : false
    })
    return listResult;
}

const login = (username, password) => {
    let user = loginUsers.find( (loginUser) => {
        return (loginUser.username === username && loginUser.password === password) ? loginUser : null
    })
    return user;
}

const searchUserLessApi = (searchValue) => {
    console.log(searchValue)
    // return axios.get(`https://tiktok.fullstack.edu.vn/api/users/search?q=${encodeURIComponent(searchValue)}&type=less`)
    return axios.get(`https://tiktok.fullstack.edu.vn/api/users/search`, {
        params: {
            q: searchValue,
            type: "less"
        }
    })
    .then( response => {
        if(response.status === 200) {
            return  response
        } else {
            return ''
        }
    }).catch( error => {
        console.log(error);
    })
};


// eslint-disable-next-line import/no-anonymous-default-export
export default {
    searchUser,
    login,
    searchUserLessApi
};
