            { name: "vy", avatar: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQnuXB-rgZwsrCtrXW-DwV4b6mjNZqTssy5aQ&usqp=CAU' },
            { name: 'iuuu', avatar: 'https://cdn.24h.com.vn/upload/2-2021/images/2021-05-22/anh-8-1621645023-458-width650height813.jpg' },
          ];
          users.map( (user) => {
            if (user.name.toLowerCase().includes(value.toLowerCase()) || user.name.toLowerCase() === value.toLowerCase()) {
                listResult.push(user)
            }
        })
    
    return listResult;
}

function sleep(ms) {
    return new Promise(resolve => setTimeout(resolve, ms));
 }

 export default searchUser;
