import styles from "./HeaderLogin.module.scss";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEllipsisVertical, faQuestionCircle,faA, faKeyboard } from '@fortawesome/free-solid-svg-icons'
import LoginButton from "../LoginButton";
import UploadButton from "../UploadButton";
import Menu from "../Menu";
import Tippy from '@tippyjs/react/headless';
// import { useState } from "react";

const MENUS = [
  {
      title: "English",
      iconSeeMore: <FontAwesomeIcon className={styles["icon"]} icon={faA} />,
      children: [
        { title: "English" , code: 'en'},
        { title: "Việt Nam" , code: 'vi'},
        { title: "日本" , code: 'ja'},
        // { title: "English" , code: 'en'},
      ],
  },
  { title: "Feedback and help", iconSeeMore: <FontAwesomeIcon className={styles["icon"]} icon={faQuestionCircle}/>},
  { title: "Keyboard shortcuts", iconSeeMore: <FontAwesomeIcon className={styles["icon"]} icon={faKeyboard}/>},
]

function HeaderLogin() {

    const _props = {
        onclick,
    };

    return ( 
        <>
          <UploadButton to="/upload" stylesupload='wrapper'>Upload</UploadButton>
          <LoginButton {..._props}>Login</LoginButton>
          <Tippy
          // visible
            interactive
            delay={200}
            placement="right-end"
            render={attrs => (
                  <div className={styles['see-more-popup']} tabIndex="-1" {...attrs}>
                    <Menu menus={MENUS} isHide={false} />
                </div>
            )}
            onHidden={
              () => {
                <Menu hide={0} isHide={false}/>
              }
            }
          >
            <div className={styles['see-more-icon']}>
              <FontAwesomeIcon icon={faEllipsisVertical} />
            </div>
          </Tippy>
        </>
     );
}

export default HeaderLogin;
