import styles from './Menu.module.scss';
import { useState } from 'react';
import MenuItem from '../MenuItem'
import HeaderMenuLangue from '../HeaderMenuLangue';
import Tippy from '@tippyjs/react/headless';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEllipsisVertical, faQuestionCircle,faA, faKeyboard } from '@fortawesome/free-solid-svg-icons'

const MENUS = [
    {
        title: "English",
        iconSeeMore: <FontAwesomeIcon className={styles["icon"]} icon={faA} />,
        children: [
          { title: "English" , code: 'en'},
          { title: "Việt Nam" , code: 'vi'},
          { title: "日本" , code: 'ja'},
          // { title: "English" , code: 'en'},
        ],
    },
    { title: "Feedback and help", iconSeeMore: <FontAwesomeIcon className={styles["icon"]} icon={faQuestionCircle}/>},
    { title: "Keyboard shortcuts", iconSeeMore: <FontAwesomeIcon className={styles["icon"]} icon={faKeyboard}/>},
  ]
function Menu({ menus, hide, isHide }) {
    const [menu, setMenu] = useState([{ data: menus }]);
    const [isLanguage, setIsLanguage] = useState(false);
    // const [isLanguage, setIsLanguage] = useState(false);

    if(!isHide) {
        console.log("tng")
        console.log(menu[0])
    }

    const currentMenu = menu[menu.length - 1];

    const renderMenuItem = () => {
        return currentMenu.data.map((menuItem, index) => {
            if (menuItem.children) {
                return (
                    <div>
                        <MenuItem key={index} data={menuItem} onclick={() => {
                            setIsLanguage(true)
                            setMenu([...menu, { data: menuItem.children }])
                        }} />
                    </div>
                )
            } else {
                return (
                    <MenuItem key={index} data={menuItem} />
                )
            }
        })
    }

    const checkHeader = () => {
        if (isLanguage) {
            return (
                <>
                    <HeaderMenuLangue onback={() => {
                        setIsLanguage(false)
                        setMenu(menu.splice(0, menu.length - 1))
                    }} />
                    {renderMenuItem()}
                </>
            );
        } else {
            return (
                <>
                    {renderMenuItem()}
                </>
            );
        }
    }
    return (
        <Tippy
          // visible
            interactive
            delay={200}
            placement="right-end"
            render={attrs => (
                  <div className={styles['see-more-popup']} tabIndex="-1" {...attrs}>
                     <div className={styles['wrapper']}>
            {checkHeader()}
        </div>
                </div>
            )}
            onHidden={ () => {
              return (
                <div className={styles['see-more-popup']}>
                    <Menu menus={MENUS} isHide={true} />
                </div>
              )
            }}
          >
            <div className={styles['see-more-icon']}>
              <FontAwesomeIcon icon={faEllipsisVertical} />
            </div>
          </Tippy>
       
    );
}

export default Menu;
