import styles from './Popup.module.scss';
import { useState } from 'react';
// import { useEffect } from 'react';

function Popup(prop) {
    const [displayPopup, setDisplayPopup] = useState(prop.display);
    
    // useEffect(() => {
    //     setDisplayPopup()
    // }, [displayPopup]);

    // console.log(_props.display)
    return (
        <div className={styles[displayPopup]}>
            <h2>Hello</h2>
            <button onClick={
                () => {
                    setDisplayPopup('wrapper-display-none')
                }
            }>click</button>
        </div>
     );
}

export default Popup;
