import styles from "./Header.module.scss";
import { useState } from "react";
import { useEffect } from "react";
// import classNames from "classnames/bind";
import images from "../../../../assets/img";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faMagnifyingGlass, faCircleXmark, faSpinner, faEllipsisVertical } from '@fortawesome/free-solid-svg-icons'
import Search from "../../Search";
import LoginButton from "../../../LoginButton";
import UploadButton from "../../../UploadButton";
import SeeMore from "../../../SeeMore";
import Tippy from "@tippyjs/react/headless";
// import './tung.css'
// const cx = classNames.bind(styles);
function Header() {
  const [searchValue, setSearchValue] = useState('');
  const [clearSpinner, setClearSpinner] = useState(faCircleXmark);
  const [listData, setListData] = useState([]);
  const [opacity, setOpacity] = useState(2);

  useEffect(() => {
    if (searchValue !== '') {
      setClearSpinner(faSpinner)
      setListData(["f"])
    }
  }, [searchValue]);

  useEffect(() => {
    if (listData.length !== 0 && listData !== undefined) {
      setClearSpinner(faCircleXmark)
    }
  }, [listData]);

  let clearInput = (e) => {
    e.preventDefault();
    setListData([])
    setClearSpinner(faCircleXmark)
    setSearchValue('')
  }

  return (
    <header className={styles["wrapper"]}>
      <div className={styles["content"]}>
        <div className={styles["logo"]}>
          <img src={images.logo} alt="Tiktok" />
        </div>
        <div className={styles['search']}>
          <form className={styles['search-form']}>
            <input
              id="search-input"
              name="txt-search"
              type="text"
              className={styles['search-input']}
              placeholder="Search accounts and videos"
              spellCheck={false}
              onChange={(e) => {
                setSearchValue(e.target.value)
              }}
              value={searchValue}
            />
            <button className={styles['search-clear']} onClick={(e) => {
              clearInput(e)
            }}>
              <FontAwesomeIcon icon={clearSpinner} />
            </button>
            <FontAwesomeIcon className={styles['search-spinner']} icon={faSpinner} />
            <span className={styles['span-splitter']}></span>
            <button className={styles['btn-search']} type="submit">
              <FontAwesomeIcon icon={faMagnifyingGlass} />
            </button>
          </form>
          <div className={styles['display-list__find']}>
            <Search searchValue={searchValue} />
          </div>
        </div>
        <div className={styles['action']}>
          <UploadButton />
          <LoginButton />
          <Tippy
            interactive
            visible
            render={(attrs) => {
              <>
                <div id="check" className={styles['see-more-popup']}>
                  <h2>fsdfsdf</h2>
                  {/* <SeeMore /> */}
                </div>
              </>
            }}
          >
            <div id="see-more-icon" className={styles['see-more-icon']} display-none>
              <FontAwesomeIcon icon={faEllipsisVertical} />

            </div>
          </Tippy>

        </div>

      </div>
    </header>
  );
}

export default Header;
