import styles from './Search.module.scss'
import { faMagnifyingGlass } from '@fortawesome/free-solid-svg-icons';
import Proper from '../../Proper';

function Search(prop) {
  let list = [
    { name: "Tung", avatar: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQnuXB-rgZwsrCtrXW-DwV4b6mjNZqTssy5aQ&usqp=CAU' },
    { name: "kieu", avatar: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQnuXB-rgZwsrCtrXW-DwV4b6mjNZqTssy5aQ&usqp=CAU' },
    { name: "liem", avatar: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQnuXB-rgZwsrCtrXW-DwV4b6mjNZqTssy5aQ&usqp=CAU' },
    { name: "tu", avatar: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQnuXB-rgZwsrCtrXW-DwV4b6mjNZqTssy5aQ&usqp=CAU' },
    { name: "vy", avatar: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQnuXB-rgZwsrCtrXW-DwV4b6mjNZqTssy5aQ&usqp=CAU' },
    { name: 'iuuu', avatar: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQnuXB-rgZwsrCtrXW-DwV4b6mjNZqTssy5aQ&usqp=CAU' },
  ];

  let listResultRender = (value) => {
    if (value) {
      return (

        <>
          {list.map((result, index) => {
            if (prop.searchValue) {
              if (result.name.toLowerCase().includes(value.toLowerCase()) || result.name.toLowerCase() === value.toLowerCase()) {
                return (
                  <Proper searchValue={result.name} icon={faMagnifyingGlass} />
                );
              }
            }
          })}
          <p key={value} className={styles['show-result']}>View all results for "{value}"</p>
        </>
      );
    }
  }

  return (
    listResultRender(prop.searchValue)
  );
}

export default Search;
