import styles from "./Header.module.scss";
import { useState } from "react";
import { useEffect } from "react";
import Tippy from "@tippyjs/react/headless";
// import classNames from "classnames/bind";
import images from "../../../../assets/img";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faMagnifyingGlass, faCircleXmark, faSpinner } from '@fortawesome/free-solid-svg-icons'
import SearchRender from "../../SearchRender";
import ApiService from '../../../../services/ServiceApi'
import HeaderLogin from "../../../HeaderLogin";
import HeaderLogged from "../../../HeaderLogged";
import Popup from "../../../PopupLogin";
// const cx = classNames.bind(styles);
function Header() {
  const [searchValue, setSearchValue] = useState('');
  const [clearSpinner, setClearSpinner] = useState('');
  const [listData, setListData] = useState([]);
  const [displayPopLogin, setDisplayPopLogin] = useState('display-none');
  const [tung, setTung] = useState([]);
  const [timeDelay, settimeDelay] = useState(0);
  const searchApi = async () => {
    await ApiService.searchUserLessApi().then(response => {
      setTung(response.data)
    }
    ).catch(() => {
      console.log("adas")
    })
  }

  useEffect(() => {
    if (searchValue === '') {
      setListData([])
      setClearSpinner('')
    } else if (searchValue !== '') {
      const handle = setTimeout(() => {
        // searchApi();
        // console.log(tung)
        setClearSpinner(faSpinner)
        setListData(ApiService.searchUser(searchValue))
        setClearSpinner(faCircleXmark)
      }, 3000)
      return () => {
        clearTimeout(handle);
      }
    }
  }, [searchValue]);

  const clearInput = (e) => {
    e.preventDefault();
    setListData([])
    setClearSpinner(faCircleXmark)
    setSearchValue('')
  }

  const checkClearSpinner = () => {
    if (clearSpinner !== '') {
      return <FontAwesomeIcon icon={clearSpinner} />
    }
    return
  }

  return (
    <header className={styles["wrapper"]}>
      <div className={styles["content"]}>
        <div className={styles["logo"]}>
          <img src={images.logo} alt="Tiktok" />
        </div>
        <div className={styles['search']}>
          <form className={styles['search-form']}>
            <div className={styles['search-input']}>
              <Tippy
                // visible={ listData.length > 0 }
                delay={timeDelay}
                interactive
                placement="left"
                render={attrs => (
                  <div tabIndex="-1" {...attrs}>
                    <SearchRender list={listData} searchValue={searchValue} />
                  </div>
                )}
                onShow={() => {
                  settimeDelay(2000)
                }}
                onHidden={() => {
                  settimeDelay(0)
                }}
              >
                <>
                  <input id="search-input"
                    name="txt-search"
                    type="text"
                    className={styles['search-input']}
                    placeholder="Search accounts and videos"
                    spellCheck={false}
                    onChange={(e) => {
                      setSearchValue(e.target.value)
                    }}
                    value={searchValue}
                  />
                </>
              </Tippy>
            </div>
            <FontAwesomeIcon className={styles['search-spinner']} icon={faSpinner} />
            <span className={styles['span-splitter']}></span>
            <button className={styles['btn-search']} type="submit">
              <FontAwesomeIcon icon={faMagnifyingGlass} />
            </button>
          </form>
        </div>
        <div className={styles['action']}>
          {/* <HeaderLogin onclick={() => {
            setDisplayPopLogin('display-block')
          }} /> */}
          <HeaderLogged />
        </div>
      </div>
      <Popup onclose={() => {
        setDisplayPopLogin('display-none')
      }} classnames={displayPopLogin} />
    </header>
  );
}

export default Header;
