import styles from './Menu.module.scss';
import { useState } from 'react';
import MenuItem from '../MenuItem'
import HeaderMenuLangue from '../HeaderMenuLangue';
function Menu({ menus }) {
    const [menu, setMenu] = useState([{ data: menus }]);
    const [isLanguage, setIsLanguage] = useState(false);
    const [styleRow, setStyleRow] = useState('');
    const currentMenu = menu[menu.length - 1];

    const renderMenuItem = (style) => {
        return currentMenu.data.map((menuItem, index) => {
            if (menuItem.children) {
                return (
                    <div className={styles['header-menu']}>
                        <MenuItem stylerow={style} key={index} data={menuItem} onclick={() => {
                            setIsLanguage(true)
                            setMenu([...menu, { data: menuItem.children }])
                        }} />
                    </div>
                )
            } else {
                return (
                        <MenuItem stylerow={style} key={index} data={menuItem} />
                )
            }
        })
    }

    const checkHeader = (style) => {
        if (isLanguage) {
            
            return (
                <>
                    <HeaderMenuLangue onback={() => {
                        setIsLanguage(false)
                        setMenu(menu.splice(0, menu.length - 1))
                    }} />
                    {renderMenuItem(style)}
                </>
            );
        } else {
            setStyleRow('wrapper-row-item')
            return (
                <>
                    {renderMenuItem(style)}
                </>
            );
        }
    }
    return (
        <div className={styles['wrapper']}>
            {checkHeader(styleRow)}
        </div>
    );
}

export default Menu;
