import styles from './Search.module.scss'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faMagnifyingGlass } from '@fortawesome/free-solid-svg-icons';

function Search(prop) {
  let list = [
    { name: "tung", avatar: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQnuXB-rgZwsrCtrXW-DwV4b6mjNZqTssy5aQ&usqp=CAU' },
    { name: "kieu", avatar: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQnuXB-rgZwsrCtrXW-DwV4b6mjNZqTssy5aQ&usqp=CAU' },
    { name: "tffffk", avatar: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQnuXB-rgZwsrCtrXW-DwV4b6mjNZqTssy5aQ&usqp=CAU' },
    { name: "tung", avatar: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQnuXB-rgZwsrCtrXW-DwV4b6mjNZqTssy5aQ&usqp=CAU' },
  ];

  let listResultRender = (value) => {
    if (value) {
      return (
        <>
          <ul>
            {list.map((result, index) => {
              if (prop.searchValue) {
                if (result.name.includes(value) || result.name === value) {
                  return (
                    <li key={result.name + '-' + index} className={styles['search-list_result']}>
                      <FontAwesomeIcon className={styles['search-list__result__find']} icon={faMagnifyingGlass} />
                      <p className={styles['search-list_result__value']}>{result.name}</p>
                    </li>
                  );
                }
              }
            })}
          </ul>
          <p className={styles['show-result']}>View all results for "{value}"</p>
        </>
      );
    }
  }

  return (
    <div className={styles['search-list']}>
        {
listResultRender(prop.searchValue)
        }
    </div>
  );
}

export default Search;
