];

let searchUser = (value) => {
    let listResult = [];
    users.map(user => {
        if (user.name.toLowerCase().includes(value.toLowerCase()) || user.name.toLowerCase() === value.toLowerCase()) {
            listResult.push(user)
            return user;
        }
    })
    return listResult;
}


// eslint-disable-next-line import/no-anonymous-default-export
export default {
    searchUser
};
